import React, { Component } from 'react';
import { Route, Switch, Redirect, withRouter } from 'react-router-dom';

import Backdrop from "./comp/backdrop/backdrop.js";
import Loginpage from "./comp/float-pages/loginpage/loginpage.js";
import './App.scss';
import Homepage from './comp/Pages/homepage/homepage.js';
import Gallerypage from './comp/Pages/gallerypage/gallerypage.js';
import Profilepage from './comp/Pages/profilepage/profilepage.js';

class App extends Component {
  state = {
    show: true
  }

  componentDidMount() {
    console.log(this.props);
  }

  componentDidUpdate() {
    console.log(this.props);
    if (!this.state.show) {
      this.setState({show:true});
    }
  }

  render() {

    const loginpage = () => {
      return(
      <Backdrop show={true} clicked={backdropShowHandler}>
        <Loginpage />
      </Backdrop>
    );};

    const backdropShowHandler = () => {
      this.setState({show:!this.state.show});
      console.log(this.state.show);
    }

    return (
      <div className="App">
        <Switch>
          <Route path="/gallery" component={Gallerypage} />
          <Route path="/profile" component={Profilepage} />
          <Route path="/" component={Homepage} />
        </Switch>
        {this.props.location.state?(
          this.state.show?
            (<Route path={this.props.location.state.Url} exact component={loginpage} />):
            (<Redirect to={this.props.location.state.preUrl}/>)
          ):("")}
      </div>
    );
  }
}

export default withRouter(App);
