import React, { Component } from 'react';
import './toolbar.scss';

class Toolbar extends Component {
    state = {check: false}

    handleCheck = (event) => {
        this.setState({check: event.target.checked});
        console.log(event.target.checked);
    }

    render() {
        return (
            <div className={this.state.check?("-hover"):("")}>
                <input type="checkbox" className="toolbar-checkbox" id ="toolbar" onChange={(e) => this.handleCheck(e)}></input>
                <label htmlFor="toolbar" className="toolbar-button">
                    <span className="toolbar-button-icon">&nbsp;</span>
                </label>
            </div>
        );
    }
}

export default Toolbar;
