import * as actionTypes from '../actions/actionType.js';

const initialState = {
    loginFrom: false,
    editItemFrom: false,
    cartPage: false,
    errorForm: {
        login: false,
        singup: false,
        cardScroll: false,
        cardBook: false,
        cardList: {
            kitten: false,
            robotV1: false,
            monster: false,
            robotV2: false,
        }
    }
};

const form = (state = initialState, action) => {
    const UpdateState = {...state};

    switch (action.type) {
        case actionTypes.OPEN_LOGIN_FORM:
            UpdateState.loginFrom = true;

            return UpdateState;
        
        case actionTypes.CLOSE_LOGIN_FORM:
            UpdateState.loginFrom = false;

            return UpdateState;

        case actionTypes.OPEN_EDIT_ITEM_FORM:
            UpdateState.editItemFrom = true;

            return UpdateState;
        
        case actionTypes.CLOSE_EDIT_ITEM_FORM:
            UpdateState.editItemFrom = false;

            return UpdateState;

        case actionTypes.OPEN_CART_PAGE:
            UpdateState.cartPage = true;

            return UpdateState;
        
        case actionTypes.CLOSE_CART_PAGE:
            UpdateState.cartPage = false;

            return UpdateState;
            
        case actionTypes.OPEN_ERROR_MESSAGE:
            if (action.character_type) {
                UpdateState.errorForm[action.comp][action.character_type] = true;
            }
            else {
                UpdateState.errorForm[action.comp] = true;
            }

            return UpdateState;

        case actionTypes.CLOSE_ERROR_MESSAGE:
            if (action.character_type) {
                UpdateState.errorForm[action.comp][action.character_type] = false;
            }
            else {
                UpdateState.errorForm[action.comp] = false;
            }

            return UpdateState;

        default:
            return state;
    }
}

export default form;
