import React , {Component} from 'react';
import Card from './card.js';
import BtnNav from '../button/btn-nav.js';
import './cardlist.scss';
import { Transform } from 'stream';
import { prependListener } from 'cluster';

class CardList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            position: 0,
            cardShow: 4,
            cardExtra: 2,
            width: props.width
            
            
        };
    }

    componentWillMount() {
        console.log(this.props.Width);
    }

    componentWillUpdate() {
        console.log(document.body.clientWidth);
    }

    moveleft = () => {
        this.setState((state)=>({position: (state.position - 1)}));
        console.log(this.state.position);
    }

    moveright = () => {
        this.setState((state)=>({position: state.position ? (state.position + 1) : -2}));
        console.log(this.state.position);
    }

    render(){
        return ( 
            <div className="list">
                <BtnNav side="left" onClick = {this.moveright} />
                <div className="list-card">
                    <div className="list-card-pocket" style={{width:(6 * 27 + "rem"), transform:(`translateX( ${ (this.state.position % 3) * 27}rem)`)}}>
                        <Card />
                        <Card />
                        <Card />
                        <Card />
                        <Card />
                        <Card />
                    </div>
                </div>
                <BtnNav side="right" onClick = {this.moveleft} />
            </div>
        );
    }
}

export default CardList;
