import React from 'react';
import "./input.scss";

const Input = (props) => {

    let inputElement = null;

    switch (props.elementType) {
        case ('input'):
            inputElement = <input 
                className="input"
                {...props.elementConfig}
                value={props.value}
                onChange={props.changed} />;
            break;

        case ('input-v1'):
            inputElement = <input 
                className={"input-v1"}
                {...props.elementConfig}
                value={props.value}
                onChange={props.changed} />;
            break;

        case ('radio-v1'):
            inputElement = props.elementConfig.options.map(option => (
                    <div 
                        className={"radio-v1"} 
                        key={option.value}
                        value={props.value}
                        >
                            <input
                                className={"radio-v1-input"}
                                type='radio'
                                id={option.value}
                                name={props.elementConfig.name}
                                onClick={props.changed}
                            >
                            </input>
                            <label
                                className={"radio-v1-label"}
                                for={option.value}
                            >{option.displayValue}</label>

                    </div>
                ))
            break;
        
        default:
            inputElement = <input 
                className="input"
                {...props.elementConfig}
                value={props.value}
                onChange={props.changed} />;
    }

    return (
        <div>
            {inputElement}
            {(props.label)?(<div className="label">{props.elementConfig.placeholder}</div>):""}
        </div>
    );
}

export default Input;
