import React , {Component} from 'react';
import Card from './card.js';
import BtnNav from '../button/btn-nav.js';
import './cardlist.scss';
import { Transform } from 'stream';

class CardList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {position: 0};
    }

    moveleft() {
        this.setState((state)=>({position: state.position - 1}));
        console.log(this.state.position);
    }

    render(){
        return ( 
            <div className="list">
                <BtnNav />
                <div className="list-card">
                    <div className="list-card-pocket" style={{width:(6 * 27 + "rem"), transform:"translateX(" + (this.state.position * 27) + " rem)"}}>
                        <Card />
                        <Card />
                        <Card />
                        <Card />
                        <Card />
                        <Card />
                    </div>
                </div>
                <BtnNav onClick={()=>(this.moveleft())} />
            </div>
        );
    }
}

export default CardList;
