import React, { Component } from 'react';
import { connect } from 'react-redux';

import Card from './card.js';
import CardV2 from './card-v2.js';
import CardAdd from './cardadd.js';
import BtnPageNav from '../button/btn-page-nav.js';
import "./cardbook.scss";

class CardBook extends Component {
    state = {
        pages: 0,
        page_current: 1,
        num_cards_page: 8
    }

    render() {
        return (
            <div className="cardbook">
                <div>
                {this.props.user.map((card) => {return <CardV2 card={card} key={card.name}/>})}
                <CardAdd />
                </div>
                <div className="cardbook-buttons">
                    <BtnPageNav text="<" />
                    {Array.from(Array(Math.ceil(
                        this.props.user.length / this.state.num_cards_page
                        )).keys()).map((num) =>{return <BtnPageNav text={num +1} />})}
                    <BtnPageNav text=">" />
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        user: state.card.userCards,
        load: state.card.load.book
    };
}

export default connect(mapStateToProps)(CardBook);
