import React, { Component } from 'react';
import Input from './input/input.js';
import Hsecondary from "../heading-secondary/h-secondary.js";
import "./form-main.scss";

class FormMain extends Component {
    state = {
        signupForm: {
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'Enter your Email'
                },
                value: '',
                validation: {
                    required: true,
                    isEmail: true
                },
                valid: false,
                touched: false
            }
        }
    };

    checkValidity(value, rules) {
        let isValid = true;
        if (!rules) {
            return true
        }

        if (rules.required) {
            isValid = value.trim() !== '';
        }

        if (rules.minLength && isValid) {
            isValid = value.length >= rules.minLength;
        }

        if (rules.maxLength && isValid) {
            isValid = value.length <= rules.maxLength;
        }

        if (rules.isEmail && isValid) {
            isValid = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/.test(value);
        }

        if (rules.isNumeric && isValid) {
            isValid = /^\d+$/.test(value)
        }
    }

    render () {
        return (
            <div className="form-main">
                <div className="form-main-signup">
                    <Hsecondary text="New Account"/>
                    <Input elementType='input-v1' value="pls enter something :)" />
                    <Input elementType='input-v1' value="pls enter something :)" />
                </div>
                <div className="form-main-content">
                    <h2 className="form-main-content-h">Sign up now!</h2>
                    <p className="form-main-content-t">fdsfdsfsdfsd df sdfsdfsdf sdffdffgfgfd ggfd gdfg dfg   gfgdfgdfg</p>
                </div>
            </div>
        );
    }
};

export default FormMain;
