import * as actionTypes from './actionType.js';
import axios from 'axios';

//export const addCardToTheDeck;

export const addCard = (imgpath, itemselection, itemname, itemdescription, itemprice, userId) => {
    return (dispatch) => {
        console.log(imgpath, itemselection, itemname, itemdescription, itemprice, userId);
        axios.post('http://localhost:3001/addcard', {
            name: itemname,
            description: itemdescription,
            price: itemprice,
            character_type: itemselection,
            image_path: imgpath,
            owner_id: userId
        })
        .then(card => {
            console.log(card);
        })
        .catch(error => 
            //dispatch(signupFailed(error))
            console.log(error)
            );
    }
};

export const getUserCards = (userId) => {
    return (dispatch) => {
        console.log(userId + "hi");
        axios.get('http://localhost:3001/get_user_cards', {
            owner_id: userId
        })
        .then(card => {
            console.log(card);
        })
        .catch(error => 
            //dispatch(signupFailed(error))
            console.log(error)
            );
    }
};
