import React from 'react';
import "./input.scss";

const Input = (props) => {

    let inputElement = null;

    switch (props.elementType) {
        case ('input'):
            inputElement = <input 
                className="input"
                {...props.elementConfig}
                value={props.value}
                onChange={props.changed} />;
            break;

        case ('input-v1'):
            inputElement = <input 
                className={"input-v1"}
                {...props.elementConfig}
                value={props.value}
                onChange={props.changed} />;
            break;

        case ('radio-v1'):
            inputElement = props.elementConfig.options.map(option => (
                    <div 
                        className={"radio-v1"} 
                        key={option.text}
                        value={props.value}
                        >
                            <input
                                className={"radio-v1-input"}
                                type={'radio'}
                                id={option.text}
                                name={props.elementConfig.name}
                                onChange={props.changed}
                                value={option.value}
                            >
                            </input>
                            <label
                                className={"radio-v1-label"}
                                htmlFor={option.text}
                            >
                                <span className={"radio-v1-button"}></span>
                                {option.displayText}
                            </label>

                    </div>
                ))
            break;
        
        case ('radio-v2'):
            inputElement = props.elementConfig.options.map((option ,index) => (
                    <div 
                        className={"radio-v2"} 
                        key={option.text}
                        value={props.value}
                        >
                            <input
                                key={option.text}
                                className={"radio-v2-input"}
                                type={'radio'}
                                id={option.text}
                                name={props.elementConfig.name}
                                checked={props.value === option.text}
                                onChange={(event) => (props.changed(event, index))}
                                value={props.value}
                            >
                            </input>
                            <label
                                className={"radio-v2-label"}
                                htmlFor={option.text}
                            >
                                {option.displayText}
                            </label>

                    </div>
                ))
            break;
        
        case ('checkbox-v1'):
            inputElement = props.elementConfig.options.map((option, index) => (
                    <div 
                        className={"checkbox-v1"} 
                        key={option.text}
                        value={props.value}
                        >
                            <input
                                I={option.id}
                                className={"checkbox-v1-input"}
                                type={'checkbox'}
                                id={option.text}
                                name={props.elementConfig.name}
                                onChange={(event) => (props.changed(event, index))}
                                value={option.value}
                            >
                            </input>
                            <label
                                className={"checkbox-v1-label"}
                                htmlFor={option.text}
                            >
                                {option.displayText}
                            </label>

                    </div>
                ))
            break;
        
        default:
            inputElement = <input 
                className="input"
                {...props.elementConfig}
                value={props.value}
                onChange={props.changed} />;
    }

    return (
        <div>
            {inputElement}
            {(props.label)?(<div className="label">{props.elementConfig.placeholder}</div>):""}
        </div>
    );
}

export default Input;
