import React, { Component } from 'react';
import Input from '../../form/input/input.js';
import Hsecondary from "../../heading-secondary/h-secondary.js";
import BtnAn from "../../button/btn-an.js";
import "./edit-item-page.scss";


class Edititempage extends Component {
    state = {
        signupForm: {
            imgpath: {
                elementType: 'input-v1',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Enter item image name'
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
                touched: false,
                label: true
            },
            
            itemselection: {
                elementType: 'select-v1',
                elementConfig: {
                    options: [
                        {displayText: "RobotV1", value:'robotV1'},
                        {displayText: "Monster", value:'monster'},
                        {displayText: "RobotV2", value:'robotV2'},
                        {displayText: "Kitten", value:'kitten'}
                    ]
                },
                value: '',
                validation: {},
                valid: true,
                touched: false,
                label: false
            },
            itemname: {
                elementType: 'input-v1',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Enter item name'
                },
                value: '',
                validation: {
                    required: true,
                },
                valid: false,
                touched: false,
                label: true
            },
            password: {
                elementType: 'textarea-v1',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Enter item description'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false,
                label: true
            }
        }
    };

    checkValidity(value, rules) {
        let isValid = true;
        if (!rules) {
            return true
        }

        if (rules.required) {
            isValid = value.trim() !== '';
        }

        if (rules.minLength) {
            isValid = value.length >= rules.minLength && isValid;
        }

        if (rules.maxLength) {
            isValid = value.length <= rules.maxLength && isValid;
        }

        if (rules.isEmail) {
            isValid = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/.test(value) && isValid;
        }

        if (rules.isNumeric) {
            isValid = /^\d+$/.test(value) && isValid;
        }

        return isValid;
    }

    inputChangedHandler = (event, inputIdentifier) => {
        const updateSignupForm = {
            ...this.state.signupForm
        };
        const updateFormElement = {
            ...updateSignupForm[inputIdentifier]
        };
        updateFormElement.value = event.target.value;
        updateFormElement.valid = this.checkValidity(updateFormElement.value, updateFormElement.validation);
        updateFormElement.touched = true;
        updateSignupForm[inputIdentifier] = updateFormElement;
        console.log(event.target.value);
        console.log(event.target.checked);

        this.setState({signupForm: updateSignupForm});
    }

    formChangedHandler = (event) => {
        console.log(event.target.value);
        this.setState({formfront: event.target.value});
    }

    render () {
        return (
            <div className="editItemPage">
                <div className="editItemPage-h"><Hsecondary text={"updating item"} /></div>
                <div className="row">
                    <div className="col-1-of-4" style={{textAlign: "center"}}>
                        <img className="card-side-front-img" alt='robots' src={`https://robohash.org/${1}?1110x1110`} />
                    </div>
                    <div className="col-1-of-2" style={{paddingTop: "1rem" ,textAlign: "left"}}>
                        {Object.keys(this.state.signupForm).map(key => (
                        <Input
                            key={key}
                            elementType={this.state.signupForm[key].elementType}
                            elementConfig={this.state.signupForm[key].elementConfig}
                            value={this.state.signupForm[key].value}
                            invalid={!this.state.signupForm[key].valid}
                            shouldValidate={this.state.signupForm[key].validation}
                            touched={this.state.signupForm[key].touched}
                            changed={(event) => this.inputChangedHandler(event, key)}
                            label={this.state.signupForm[key].label}

                            />
                        ))}
                    </div>
                </div>
                <div className="editItemPage-btn"><BtnAn color={"blue"} text={"sign up"} /></div>
            </div>
        );
    }
}

export default Edititempage;
